RuleSet.GenFile:
#!/bin/bash # # @(#) $GenPackageDir$/$GenFileName$ $MssBashScriptLicense$ $parseOracleArgs$$validateOracleArgs$ echo "Creating schema $DbSchemaName$ in database $${oradb} for $${orauser}" echo "Creating schema $DbSchemaName$ in database $${oradb} for $${orauser}" >>$${logfilename}
echo "create schema $lower DbSchemaName$" | $${sqlpluscmd} >>$${logfilename} 2>&1
if [ "$${orahost}" = "" ]; then export clioptions="" else export clioptions="-H $${orahost}" fi
$exec_CreateAllTables$$exec_CreateAllIndexes$$exec_CreateAllRelations$$exec_CreateAllSequences$$exec_CreateAllStoredProcs$$exec_LoadAllData$$exec_CreateAllTriggers$ exit 0

RuleSet.GenRule:
#
#	Create the database tables
#
if [ -f ./$dbadminfilename_CreateAllTables$ ]; then
	echo "	Creating database tables ..."
	echo "" >>$${logfilename}
	echo "#" >>$${logfilename}
	echo "#	Executing script $dbadminfilename_CreateAllTables$" >>$${logfilename}
	echo "#" >>$${logfilename}
	./$dbadminfilename_CreateAllTables$ $${clioptions} -U $${orauser} -P $${orapwd} -L $${logfilename}
fi

#
#	Create the database indexes
#
if [ -f ./$dbadminfilename_CreateAllIndexes$ ]; then
	echo "	Creating database indexes ..."
	echo "#" >>$${logfilename}
	echo "#	Executing script $dbadminfilename_CreateAllIndexes$" >>$${logfilename}
	echo "#" >>$${logfilename}
	./$dbadminfilename_CreateAllIndexes$ $${clioptions} -U $${orauser} -P $${orapwd} -L $${logfilename}
fi

#
#	Create the database relations
#
if [ -f ./$dbadminfilename_CreateAllRelations$ ]; then
	echo "	Creating database relations ..."
	echo "" >>$${logfilename}
	echo "#" >>$${logfilename}
	echo "#	Executing script $dbadminfilename_CreateAllRelations$" >>$${logfilename}
	echo "#" >>$${logfilename}
	./$dbadminfilename_CreateAllRelations$ $${clioptions} -U $${orauser} -P $${orapwd} -L $${logfilename}
fi

#
#	Create the database sequences
#
if [ -f ./$dbadminfilename_CreateAllSequences$ ]; then
	echo "	Creating database sequences..."
	echo "" >>$${logfilename}
	echo "#" >>$${logfilename}
	echo "#	Executing script $dbadminfilename_CreateAllSequences$" >>$${logfilename}
	echo "#" >>$${logfilename}
	./$dbadminfilename_CreateAllSequences$ $${clioptions} -U $${orauser} -P $${orapwd} -L $${logfilename}
fi

#
#	Create the database stored procedures
#
if [ -f ./$dbadminfilename_CreateAllStoredProcs$ ]; then
	echo "	Creating database stored procedures..."
	echo "" >>$${logfilename}
	echo "#" >>$${logfilename}
	echo "#	Executing script $dbadminfilename_CreateAllStoredProcs$" >>$${logfilename}
	echo "#" >>$${logfilename}
	./$dbadminfilename_CreateAllStoredProcs$ $${clioptions} -U $${orauser} -P $${orapwd} -L $${logfilename}
fi

#
#	Load initial data
#
if [ -f ./$dbadminfilename_LoadAllData$ ]; then
	echo "	Loading initial data ..."
	echo "" >>$${logfilename}
	echo "#" >>$${logfilename}
	echo "#	Executing script $dbadminfilename_LoadAllData$" >>$${logfilename}
	echo "#" >>$${logfilename}
	./$dbadminfilename_LoadAllData$ $${clioptions} -U $${orauser} -P $${orapwd} -L $${logfilename}
fi

#
#	Create the database triggers
#
if [ -f ./$dbadminfilename_CreateAllTriggers$ ]; then
	echo "	Creating database triggers ..."
	echo "" >>$${logfilename}
	echo "#" >>$${logfilename}
	echo "#	Executing script $dbadminfilename_CreateAllTriggers$" >>$${logfilename}
	echo "#" >>$${logfilename}
	./$dbadminfilename_CreateAllTriggers$ $${clioptions} -U $${orauser} -P $${orapwd} -L $${logfilename}
fi
