create or replace function $lower DbSchemaName$.$sp_next_tableidgen$(
	$reference Dispenser reference PrimaryKeyIndex iterate Columns( first sparg_TableCol each sparg_CommaTableCol )$ )
return smallint authid definer
is
	SliceId smallint := $Slice$;
	NextId smallint;
	CountEm integer;
begin
	select count(*)
	into CountEm
	from $lower DbSchemaName$.$lower reference Dispenser DbTableName$_idg16
	where slice_id = SliceId$reference Dispenser reference PrimaryKeyIndex iterate Columns( each CreateSPNextTableIdGenPassExistsArg )$;

	if CountEm < 1 then
		insert into $lower DbSchemaName$.$lower reference Dispenser DbTableName$_idg16 ($reference Dispenser reference PrimaryKeyIndex iterate Columns( each CreateSPNextTableIdGenInsertCol )$
			slice_id,
			next_id )
		values ($reference Dispenser reference PrimaryKeyIndex iterate Columns( each CreateSPNextTableIdGenInsertArg )$
			SliceId,
			1 );
	end if;

	select next_id
	into NextId
	from $lower DbSchemaName$.$lower reference Dispenser DbTableName$_idg16
	where slice_id = SliceId$reference Dispenser reference PrimaryKeyIndex iterate Columns( each CreateSPNextTableIdGenPassWhereArg )$
	for update;

	update $lower DbSchemaName$.$lower reference Dispenser DbTableName$_idg16
	set next_id = NextId + 1
	where slice_id = SliceId$reference Dispenser reference PrimaryKeyIndex iterate Columns( each CreateSPNextTableIdGenPassWhereArg )$;

	return NextId;
end $sp_next_tableidgen$;
/

show errors function $lower DbSchemaName$.$sp_next_tableidgen$;

