RuleSet.GenRule:
# # Parse the command line options # while [ -n "$$1" ] do case "$$1" in
-D) shift pgdatadir=$$1 shift ;;
-d) shift pgdb=$$1 shift ;;
-L) shift logfilename=$$1 shift ;;
-H) shift pghost=$$1 shift ;;
-p) shift pgport=$$1 shift ;;
-P) shift pgpwd=$$1 shift ;;
-S) shift pgsrv=$$1 shift ;;
-U) shift pguser=$$1 shift ;;
*) echo >&2 "ERROR: Option $$1 not recognized." parmerrors="Y" shift ;; esac done

#
#	Recreate the database instance
#
echo "Recreating database $lower DbSchemaName$ ..." | tee -a $${logfilename}

echo "" >>$${logfilename}
echo "#" >>$${logfilename}
echo "#	Drop database $lower DbSchemaName$" >>$${logfilename}
echo "#" >>$${logfilename}

psql -h $${pghost} $${PGPORT} -d template1 -U $${pguser} -a -e -c "drop database $lower DbSchemaName$" >>$${logfilename} 2>&1

echo "" >>$${logfilename}
echo "#" >>$${logfilename}
echo "#	Create database $lower DbSchemaName$" >>$${logfilename}
echo "#" >>$${logfilename}

psql -h $${pghost} $${PGPORT} -d template1 -U $${pguser} -a -e -c "create database $lower DbSchemaName$ tablespace $lower DbSchemaName$_data1" >>$${logfilename} 2>&1

./$dbadminfilename_CreateSchema$ -H $${pghost} $${PASSPGPORT} -d $lower DbSchemaName$ -U $${pguser} -P $${pgpwd} -L $${logfilename}
